graphql.query:
query contactMethods ($transformation: Transformation) {
    contactMethods {
        method
        iconImage {
            url (transformation: $transformation)
        }
        url
    }
}

query developer ($transformation: Transformation, $transformation1: Transformation, $where: Name!) {
    developer (where: $where) {
        slug
        name
        about
        location {
            latitude
            longitude
        }
        images {
            url (transformation: $transformation)
        }
        profilePicture {
            url (transformation: $transformation1)
        }
        email
        portfolio
        github
        LinkedIn
        otherWebsites
    }
}

query projects ($transformation: Transformation) {
    projects {
        slug
        projectName
        previewImages {
            url (transformation: $transformation)
        }
        Description
        tags
        demo
        sourceCode
    }
}

query posts ($transformation: Transformation) {
    posts {
        slug
        title
        subtitle
        category
        coverImage {
            url (transformation: $transformation)
        }
        content
        tags
        source
    }
}
